import React,{ useState, useEffect } from 'react';
import { Link, navigate } from '@reach/router';
import axios from 'axios';


export default (props) => {

    const [name, setName] = useState('');
    const [due_date, setDue_date] = useState('')

    const onSubmit = (e, data) => {
        e.preventDefault();
        axios.post('http://localhost:8000/api/projects', data)
        .then(res => {
            navigate('/');
        })
        .catch(err => {
            const errorResponse = err.response.data.errors;
            const errorArr=[];

            for(const key of Object.keys(errorResponse)){
                errorArr.push(errorResponse[key].message)
            }
            setErrors(errorArr);
        })
    }





    return(
        <div className='container'>
            <Link to ='/'>Back to Dashboard</Link>
            <div className='form'>
                <form onSubmit={onSubmit}>
                    <div>
                        <label htmlFor='name'>Project: </label>
                        <input type ='text' name='name' onChange={(e)=> {setName(e.target.value)}} />
                    </div>
                    <div> 
                        <lable htmlFor='due_date'>Due Date: </lable>
                        <input type='date' name='due_date' onChange={(e)=> {setDue_date(e.target.value)}} />
                    </div>
                    <button className='btn btn-primary btn-md'>Submit</button>
                </form>
            </div>

        </div>
    )

}
