import React,{ useState, useEffect } from 'react';
import { Link, navigate } from '@reach/router';
import axios from 'axios';

export default (props) => {
    const[project, setProject] = useState('');
    const[loaded, setLoaded] = useState(false);


    useEffect(() => {
        let notStarted =[];
        let notCompleted =[];
        let completed =[];
        axios.get('http://localhost:8000/api/projects')
        .then(res => {
            setProject(res.data);
            setLoaded(true)
            res.data.map((item, i) => {
                if(item.isStarted == false){
                    notStarted.push(item)
                }
                else if(item.isStarted == true && item.isComplete == false){
                    notCompleted.push(item)
                }
                else if(item.isComplete == true){
                    completed
                }
            })
        })
    }, []);

    const addNew = (e) => {
        navigate('/create')
    }


    return(
        <div>
            

            {loaded && <div>
                {project.map((item, i) => { return <div key={i}>
                <p>{item.name}</p></div>})}
            </div>}


            <div>
                <button className='btn btn-primary btn-sm'onClick={addNew}>Add New Project</button>
            </div>
        </div>

    )
}
