import React,{ useState, useEffect } from 'react';
import { Link, navigate } from '@reach/router';
import axios from 'axios';

export default (props) => {
    const[project, setProject] = useState('');
    const[loaded, setLoaded] = useState(false);
    const[projectCompleted, setProjectCompleted] = useState({});
    const[projectStarted, setProjectStarted] = useState({});
    const[projectBacklog, setProjectBacklog] = useState({});


    useEffect(() => {
        let notStarted =[];
        let notCompleted =[];
        let completed =[];
        axios.get('http://localhost:8000/api/projects')
        .then(res => {
            setProject(res.data);
            res.data.map((item, i) => {
                if(item.isStarted == false){
                    notStarted.push(item)
                }
                else if(item.isStarted == true && item.isComplete == false){
                    notCompleted.push(item)
                }
                else if(item.isComplete == true && item.isStarted == true){
                    completed.push(item)
                }
            })
            setProjectBacklog(notStarted);
            setProjectStarted(notCompleted);
            setProjectCompleted(completed);
            setLoaded(true)
        })
    }, []);

    const addNew = (e) => {
        navigate('/create')
    }

    return(
        <div>
            {loaded && <div style={{display: 'inline-block'}}>
                {projectBacklog.map((item, i) => { return <ul key={i} style={{border: '1px solid black', padding:'10px'}}>
                    <li>Project: {item.name}</li>
                    <li>Due by: {item.due_date}</li>
                </ul>})}
            </div>}

            {loaded && <div style={{display: 'inline-block', marginRight:'20px', marginLeft: '20px'}}>
                {projectStarted.map((item, i) => { return <ul key={i} style={{border: '1px solid black', padding:'10px'}}>
                    <li>Project: {item.name}</li>
                    <li>Due by: {item.due_date}</li>
                </ul>
            })}
            </div>}

            {loaded && <div style={{display: 'inline-block'}}>
                {projectCompleted.map((item, i) => { return <div key={i} style={{border: '1px solid black', padding:'10px'}}>
                    <p>Project: {item.name}</p>
                    <p>Due by: {item.due_date}</p>
                </div>})}
            </div>}


            <div>
                <button className='btn btn-primary btn-sm'onClick={addNew}>Add New Project</button>
            </div>
        </div>

    )
}
